      this.formFieldService.control.errorStateChanges.subscribe(errors => {
        this.errors = errors;
        this.cdr.detectChanges();
      });
    }
  }

  hasError(name: string): boolean {
    return this.errors.indexOf(name) !== -1;
  }
}

